sidebarCSS:
#root {
--tabs-spacing: 4px; */ Spacing between the tabs, be sure to change the tab height for it to look right!) */
}

@media screen and (max-width: 60px) {
  #root {
    --tabs-indent: unset !important;
  }

  .container { 
      max-width: 60px;
  } 

  .Tab .audio {
    margin-left: -15px;
    transform: scale(.80);
    z-index: 99 !important;
  }

    .Tab[data-lvl] , .PinnedTab[data-lvl]  {
      max-width: 30px;
      float: left;
  }
    .container { 
      margin: 0px !important;
      max-width: 50px;
    }

    .Tab[data-lvl] , .PinnedTab[data-lvl]  {
      max-width: 30px;
      float: left;
  }

    .panel-btn[data-active="true"] {
        display: ;
        display: flex !important;
        position: absolute !important;
        background: none !important;
    }


    .panel-btn {
        display: none !important;
    }

    .Sidebar .settings-btn {
    display: none;
    }

    .container::after { 
    margin-left: 16px;
    }
}

.Sidebar {
  background: var(--sidebar-background-color);
  font-family: "Segoe UI";
}

.container {
  padding-top: 2.5px !important;
  margin: 0 auto !important;
  max-width: calc(100% - 20px);
}

.NavigationBar {
   padding-left: 8px;
}


/* Reset default styles */
.Tab[data-active], .Tab:hover, .PinnedTab[data-active], .PinnedTab:hover {
  background-color: transparent;
}

.Tab[data-active] .lvl-wrapper:after, .PinnedTab[data-active] {
  background-color: var(--tabs-activated-bg);
}


/* Style for Tabs and Pinned Tabs */
.Tab .lvl-wrapper:after, .PinnedTab {
  border-radius: 5px;
  border: 1px solid transparent !important;
  border-radius: 5px;
  text-align: left;
  transition: width 0.1s !Important;
}

/* Background layer for tabs */
.Tab .lvl-wrapper:after {
  content: '';
  position: absolute;
  top: 0px;
  left: 2px;
  margin: var(--tabs-spacing) 0;
  width: calc(100% - 4px);
  height: calc(100% - calc(var(--tabs-spacing) * 2));
  z-index: -1;
}

/* Style for pinned tabs */ 

.PinnedTab {
  height: calc(var(--tabs-height) - calc(var(--tabs-spacing) * 2)) !important; 
  margin: var(--tabs-spacing) 10px;
}


.Tab[data-active] .lvl-wrapper:after, .PinnedTab[data-active] {
   box-shadow: 0px 5px 10px var(--tabs-activated-shadow) !important;
}

.Tab:hover .lvl-wrapper:after, .PinnedTab:hover {
  background: var(--tabs-bg-hover);
}

/*  Favicon Settings */

.Tab .fav, .PinnedTab .fav {
    position: relative;
    width: 18px;
    height: 18px;
    flex-shrink: 0;
    margin: 0 6px 0 6px;
    opacity: 1 !important;
}

.Tab .placeholder > svg {
  width: 18px;
  height: 18px;
}

.PinnedTab  .fav {
     margin-left: 4px;
}

.Tab:hover  .fav, .PinnedTab:hover  fav {
    opacity: 1 !important;
}

.Tab  .placeholder, .PinnedTab . placeholder {
   width: 18px;
   height: 18px;
}


/* Internal Tab Settings */ 
.Tab  .t-box, .PinnedTab  .title {
    mask: linear-gradient(-90deg,transparent,#000 25px,#000);
  /*! display: none; */
}

.Tab  .t-box .title, .PinnedTab  .title {
padding-left: 4px;
font-family: var(--tabs-font) !important;
color: var(--tabs-fg);
}

/* Tab Close and Context Buttons */
.Tab  .close {
    fill: #000;
}

.Tab  .close > svg {
    fill: #000;
    background: none;
    opacity: 1;
    height: 18px !important;
width: 18px !important;
}

.Tab  .close > svg:hover {
    background: rgba(0,0,0,0.075);
    border-radius: 4px;
fill: #000;
}

.PinnedTab  .close {
display: none !important;
}

.Tab  .ctx,
.PinnedTab  .ctx {
top: 4px;
right: auto;
left: -11px;
width: 4px;
height: calc(100% - 5px);
}

.Sidebar {
margin-top: -2.5px;
}

.PinnedDock {
padding-top: 2.5px !important;
padding-bottom: 0 !important;
margin-bottom: -2.5px;
}

.PinnedDock::after {
    display: none !important;
}

.audio {
    position: absolute;
    margin-left: -14px;
    margin-top: 7px;
    z-index: 10;
}

.audio svg {
    padding: 3px;
    height: 14px !important;
    width: 14px !important;
    background: #fff;
    border-radius: 50%;
    box-shadow: 1px 1px 5px rgba(0,0,0,0.25);

}

.Tab[data-audible]  .t-box, .Tab[data-muted] .t-box {
    transform: none !important;
}



